import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-edit-angular',
  templateUrl: './edit-angular.component.html',
  styleUrls: ['./edit-angular.component.css']
})
export class EditAngularComponent implements OnInit{
  EditAngular : any;
    mostrar: boolean = false;

    constructor(private datos: DatosService){ }

    ngOnInit(): void {
        this.datos.getDatos().subscribre(data => {
          this.EditAngular = data.EditAngular;
        })
    }
    mostrate():void{
      this.mostrar=!this.mostrar;
    }
}
